function GetPointByPincode(e) { xmlHttpRequest = window.XMLHttpRequest ? new XMLHttpRequest : new ActiveXObject("Msxml2.XMLHTTP"); if (xmlHttpRequest == null) return; xmlHttpRequest.open("GET", "/molweb/GIS/GeoAJAXHandler.aspx?PinCode=" + e, false); xmlHttpRequest.setRequestHeader("Pragma", "Cache-Control: no-cache"); xmlHttpRequest.send(null); return xmlHttpRequest.responseText } var xmlHttpRequest